import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const [nameIsValid, setNameIsValid] = useState(true);

  const nameInputRef = useRef();

  const [name, setName] = useState("");
  const formSubmitHandler = (e) => {
    e.preventDefault();
    const enterdName = nameInputRef.current.value;
    setNameIsValid(true);
    if (enterdName.trim()) {
      setNameIsValid(false);
      return;
    }
    console.log(enterdName);
    nameInputRef.current.value = "";
  };

  const onBlurHandler = () => {
    const enterdName = nameInputRef.current.value;

    setNameIsValid(true);
    if (enterdName.trim().lenght === '') {
      setNameIsValid(false);
      console.log("islogin focus");
      return;
    }
  };
  const nameInputClasses = nameIsValid
    ? "form-control"
    : "form-control invalid";

  const onChangeHandler = (e) => {
    const enterdName = nameInputRef.current.value;
    setName(e.target.value);
    setNameIsValid(true);
    if (enterdName.trim().lenght < 5) {
      setNameIsValid(false);
      console.log("islogin focus");
      return;
    }
  };
  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          onBlur={onBlurHandler}
          type="text"
          id="name"
          onChange={onChangeHandler}
        />
        {!nameIsValid && (
          <p className="error-text">Name should be more 5 characters</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
